// Parse incoming Telegram message
const items = $input.all();
const results = [];

for (const item of items) {
  const message = item.json.message;

  if (!message) {
    continue;
  }

  const text = message.text || '';
  const chatId = message.chat?.id;
  const userId = message.from?.id;
  const username = message.from?.username;
  const firstName = message.from?.first_name;

  // Parse command and parameters
  let command = '';
  let params = {};

  if (text.startsWith('/')) {
    const parts = text.split(' ');
    command = parts[0].substring(1);

    // Parse parameters based on command
    if (command === 'agendar' && parts.length >= 4) {
      params = {
        doctor: parts.slice(1, -2).join(' '),
        date: parts[parts.length - 2],
        time: parts[parts.length - 1]
      };
    } else if (command === 'verificar' || command === 'cancelar') {
      params = {
        appointmentId: parts[1]
      };
    }
  } else {
    command = 'natural';
    params = { text: text };
  }

  results.push({
    json: {
      command,
      params,
      chatId,
      userId,
      username,
      firstName,
      originalText: text,
      timestamp: new Date().toISOString()
    }
  });
}

return results;

